import React, {useState} from 'react'
import  products  from './product'

function ProductPage() {

    const [indexValue, setIndex] = useState(0)


    const handleChange = index =>{

        alert(index)

    }
    const thumbnailPhotos  = products.map((item) => {
        return item.ImageThumbSrc
    })
    console.log(thumbnailPhotos)
    return (
        <div className="product-section">
            <div className="image-wrapper">
                <div className="main-image">
                {
                    products.map((item) => {
                        const {ImageSrc} = item
                        return  <img src={ImageSrc[indexValue]} alt=""/>
                    })
                    
                }
                   

                </div>
                
               <div className="thumbnail">
               {   
                
                thumbnailPhotos.map((item ,index) =>{
                        return <img src={item} key={index} onClick={() => handleChange(index)} alt=""/>
                    })
                }
               </div>
                
            </div>
            <div className="details">

            </div>
        </div>
    )
}

export default ProductPage
