import React, {useState} from 'react'
import  products  from './product'

import plusIcon from './images/icon-plus.svg'
import minusIcon from './images/icon-minus.svg'
import cartIcon from './images/icon-cart.svg'
import {useGlobalContext} from './context'

function ProductPage() {

    const [indexValue, setIndex] = useState(0);
    
    const {counter, setCounter, cartCounter, setCartCounter, modalSlider, setModal} = useGlobalContext()



    const handleChange = index =>{

        setIndex(index)

    }
    
  
    return (
        <div className="product-section">
            <div className="image-wrapper">
                <div className="main-image">
                {
                    products.map((item) => {
                        const {ImageSrc} = item
                        return  <img src={ImageSrc[indexValue]} alt="" onClick={() => setModal(true)} />
                    })
                    
                }


                   

                </div>
                {
                    products.map((item) => {
                        return (
                            <div className="thumbnail">
                                {
                                    item.ImageThumbSrc.map((img ,index) => {
                                        return <img src={img} alt="" onClick={() => handleChange(index) }/>
                                    })
                                }
                            </div>
                    )
                    })
                }
               
                
            </div>
            <div className="details">
                <h3>Sneaker Company</h3>
                {products.map((item) =>{
                    return(  <div className="sneaker-deatils">
                    <h1>{item.title}</h1>
                    <p>{item.desc}</p>
                    <h3>${item.Previous * 0.50}</h3>
                    <del>${item.Previous}</del>
                </div>)
                      
                    
                })}

                <div className="sneaker-count">

                   <img src={minusIcon} alt="-" onClick={()=> setCounter((count)=> count > 0 ? count-1 : count)  }/> {counter}  <img src={plusIcon} alt="+" 
                   onClick={()=> setCounter((count)=> count+1 )  }/> <button onClick={() => setCartCounter(counter)} className='action-button'>  <img src={cartIcon} alt=""/>ADD TO CART</button>

                </div>

            </div>
        </div>
    )
}

export default ProductPage
