import { ListItem } from '@material-ui/core'
import React, {useState} from 'react'
import products from './product'
import {  motion } from 'framer-motion'

import nextIcon from './images/icon-next.svg'
import prevIcon from './images/icon-previous.svg'
import closeIcon from './images/icon-close.svg'
import {useGlobalContext} from './context'

function Modal() {
    const [modalCount, setModalCount] = useState(0)
    const {modalSlider, setModal} = useGlobalContext()
    return (
        <div>
        
            {
                products.map((item) => {
                    return (
                        <motion.div className={`${modalSlider ? 'modalWrapperShow' : 'modalWrapperHidden'}`}
                        transition={{duration: 1}} initial={{x: '-100vw'} } animate={{x: '100vw'} }>
                             <img src={prevIcon} alt="prev"  onClick={() => setModalCount((count) => count > 0 ? count- 1 : count =3 )}/>
                            <div>
                                <img src={closeIcon}  alt="close" onClick={() => setModal(false)}/>
                            <img src={item.ImageSrc[modalCount]} alt=""  className='modal-image'/>
                            </div>
                           
                            <img src={nextIcon} alt="next" onClick={() => setModalCount((count) => count < 3 ? count+ 1 : count = 0)}/>
                        </motion.div>
                    )
                })
            }
        </div>
    )
}

export default Modal
